function price() {
    $.getJSON("https://dividend-gold-api.herokuapp.com/price", function(e) {
        $(".eth_price").text(parseFloat(e.eth_price).toFixed(2)), $(".btc_price").text(parseFloat(e.btc_price).toFixed(2)), $(".bnb_price").text(parseFloat(e.bnb_price).toFixed(2)),
            $(".contract_balance_0x9bb").text(parseFloat(e.contract_balance_0x9bb).toFixed(2)),
            $(".market_cap_0x9bb").text(parseFloat(e.market_cap_0x9bb).toFixed(2)),
            $(".total_dgs_tokens").text(parseFloat(e.total_dgs_tokens).toFixed(2)),
            $(".buy_price_0x9bb").text(parseFloat(e.buy_price_0x9bb).toFixed(6)),
            $(".buy_price_usd_0x9bb").text(parseFloat(e.buy_price_usd_0x9bb).toFixed(2)),
            $(".sell_price_0x9bb").text(parseFloat(e.sell_price_0x9bb).toFixed(6)),
            $(".sell_price_usd_0x9bb").text(parseFloat(e.sell_price_usd_0x9bb).toFixed(2))
    })
}
price();

//iframe change
var statsChainRecord = "balance";

function balance() {
    if (statsChainRecord == "balance") {
        //do nothing
    } else {
        statsChainRecord = "balance";
        document.getElementById("myframe").src = "./charts/btc/ContractBalanceDGS.html";
        document.getElementById('balance').setAttribute('class', 'fPKMKF');
        document.getElementById('supply').setAttribute('class', 'surrJ');
        document.getElementById('pricebtc').setAttribute('class', 'surrJ');
        document.getElementById('priceusd').setAttribute('class', 'surrJ');
    }
}

